import { BrowserRouter, Switch, Route } from "react-router-dom";
import Home from "./components/Home";
import { Component } from "react";
import Login from "./components/Login";
import CartContext from "./components/ReactContext/Context";
import Cart from "./components/Cart";

class App extends Component {
  state = { cartList: [], title: "" };

  addCartItem = (item, id) => {
    const { cartList } = this.state;
    const isExist = cartList.filter((each) => each.dishId === id);
    console.log("is exist list=", isExist);
    console.log("dish id=", id);

    if (isExist.length !== 0) {
      const dish = isExist.pop();
      const index = cartList.findIndex((each) => each.dishId === id);
      console.log("index=", index);

      const { dishQuantity } = dish;
      const updatedQuantity = dishQuantity + 1;
      const newDish = { ...dish, dishQuantity: updatedQuantity };
      cartList.splice(index, 1, newDish);

      this.setState({
        cartList: [...cartList],
      });
    } else {
      this.setState({
        cartList: [...cartList, item],
      });
    }
  };

  updateTitle = (title) => {
    this.setState({
      title,
    });
  };

  removeAllCartItems = () => {
    this.setState({
      cartList: [],
    });
  };

  decrementCartItemQuantity = (id) => {
    const { cartList } = this.state;
    const dish = cartList.filter((each) => each.dishId === id);
    const { dishQuantity } = dish;
    if (dishQuantity === 1) {
      const newDishes = cartList.map((each) => each.dishId !== id);
      this.setState({
        cartList: newDishes,
      });
    } else {
      const updatedQuantity = dishQuantity - 1;
      const updatedDish = { ...dish, dishQuantity: updatedQuantity };
      this.setState({
        cartList: [...cartList, updatedDish],
      });
    }
  };

  incrementCartItemQuantity = (id) => {
    const { cartList } = this.state;
    this.setState({
      cartList: cartList.map((each) => {
        if (each.dishId === id) {
          const { dishQuantity } = each;
          return { ...each, dishQuantity: dishQuantity + 1 };
        }
        return each;
      }),
    });
  };

  render() {
    const { cartList, title } = this.state;
    console.log("cart list=", cartList);
    return (
      <>
        <CartContext.Provider
          value={{
            cartList,
            title,
            updateTitle: () => {},
            decrementCartItemQuantity: this.decrementCartItemQuantity,
            addCartItem: this.addCartItem,
            removeAllCartItems: this.removeAllCartItems,
            removeCartItem: this.removeCartItem,
            incrementCartItemQuantity: this.incrementCartItemQuantity,
          }}
        >
          <BrowserRouter>
            <Switch>
              <Route exact path="/login" component={Login} />
              <Route exact path="/" component={Home} />
              <Route exact path="/cart" component={Cart} />
            </Switch>
          </BrowserRouter>
        </CartContext.Provider>
      </>
    );
  }
}

export default App;
